import * as React from 'react';
import styles from './index.less';
import LayoutHeader from '../components/LayoutHeader';
import Profile from '../components/Profile';
import {
  CurrentTabProvider,
  useCurrentTab
} from '../utils/hooks/useCurrentTab';
import Projects from '../components/Projects';
import { ContentSwitcher } from '../components/ContentSwitcher';
import { useComponentDidMount } from '../utils/hooks/useComponentDidMount';
import { useEffect, useState } from 'react';
import debounce from 'lodash/debounce';
const DESIGN_WIDTH = 1920;

const InnerLayout: React.FC<{}> = () => {
  const {
    state: {
      scale: { scale }
    },
    actions: { setScale }
  } = useCurrentTab();
  const {IsDone, setIsDone} = useComponentDidMount(() => {
    const scale = Math.min(document.body.clientWidth / DESIGN_WIDTH, 1);
    setScale(scale);
  });
  useEffect(() => {
    setIsDone(true);
    document.getElementsByTagName('html')[0].style.fontSize = `${scale}px`;
    window.scrollTo({
      top: 0
    });
    window.onresize = debounce(() => {
      const _scale = Math.min(document.body.clientWidth / DESIGN_WIDTH, 1);
      document.getElementsByTagName('html')[0].style.fontSize = `${scale}px`;
      window.scrollTo({
        top: 0
      });
      if (_scale !== scale) {
        setIsDone(false);
        setScale(scale);
      }
    }, 300);
  }, [scale]);
  return IsDone ? (
    <div className={styles.layout}>
      <LayoutHeader />
      <ContentSwitcher>
        <>
          <Profile />
          <Projects />
        </>
      </ContentSwitcher>
      <LayoutHeader
        bodyStyle={{
          marginTop: '138rem'
        }}
      />
    </div>
  ) : null;
};

const Layout: React.FC<{}> = () => {
  return (
    <CurrentTabProvider>
      <InnerLayout />
    </CurrentTabProvider>
  );
};

export default Layout;
