import * as React from 'react';
import styles from './index.less';
import LayoutHeader from '../components/LayoutHeader';
import Profile from '../components/Profile';
import { CurrentTabProvider } from '../utils/hooks/useCurrentTab';
import Projects from '../components/Projects';
import { ContentSwitcher } from '../components/ContentSwitcher';
import { useEffect, useRef } from 'react';

const Layout: React.FC<{}> = () => {
  const containerRef = useRef(null);
  useEffect(() => {
    console.log(containerRef.current && containerRef.current.offsetWidth);
    console.log(document.getElementsByTagName('body')[0].offsetWidth);
  }, [containerRef.current]);
  return (
    <CurrentTabProvider>
      <div className={styles.layout} ref={containerRef}>
        {
          containerRef.current && containerRef.current.offsetWidth
        }
        <LayoutHeader />
        <ContentSwitcher>
          <>
            <Profile />
            <Projects />
          </>
        </ContentSwitcher>
        <LayoutHeader
          bodyStyle={{
            marginTop: '138px'
          }}
        />
      </div>
    </CurrentTabProvider>
  );
};

export default Layout;
