import React, { FC } from 'react';
import styles from './index.module.less';
import BoldenInformation from '../BoldenInformation';
import Subtitle from '../SubTitle';

export interface IPharseInfo {
  title: string[],
  subtitle?: string[],
  desc: string[],
}

interface IProps {
  info: IPharseInfo;
  align: 'left' | 'right' | 'center';
}

const PharseInformation: FC<IProps> = (props: IProps) => {
  const { info, align } = props;
  return (
    <div className={styles['info-container']}>
      <BoldenInformation informations={info.title} align={align} />
      {
        info.subtitle && <Subtitle informations={info.subtitle} align={align}/>
      }
      {
        info.desc && <Description />
      }
    </div>
  );
};

export default PharseInformation;
