import React from 'react';

const store = { currentTab: 0 };

const currentTab = (state, action) => {
  if (action.type === 'setCurrentTab') {
    return { ...state, currentTab: action.value };
  } else {
    throw new Error();
  }
};

// 自定义合并reducer函数
const combineReducers = reducers => {
  return function(state, action) {
    return Object.keys(reducers)
      .map(k => ({ [k]: reducers[k](state[k], action) }))
      .reduce((acc, cur) => Object.assign({}, acc, cur));
  };
};

const reducers = combineReducers({ currentTab });
const [_state, dispatch] = React.useReducer(reducers, store);
const _actions = {
  setCurrentTab: e => {
    dispatch({ type: 'setCurrentTab', value: e });
  }
};
// 创建Context放在最上层父组件
const CurrentTabContext = React.createContext<{
  state: typeof _state;
  actions: typeof _actions;
}>({ state: _state, actions: _actions });
const { state, actions } = React.useContext(CurrentTabContext);
export { CurrentTabContext, state, actions };
