import { useComponentDidMount } from './useComponentDidMount';

export const useResizeCallback = () => {
  let callback_queue = [];
  const { IsDone } = useComponentDidMount(() => {
    window.onresize = () => {
      callback_queue.forEach(callback => {
        callback();
      });
    };
  });
  function addCallback(callback) {
    callback_queue.push(callback);
    if (IsDone) {
      window.onresize = () => {
        callback_queue.forEach(callback => {
          callback();
        });
      };
    }
  }
  return { addCallback };
};
