import React, { FC, useState } from 'react';

export interface ISources {
  src: string;
  type: string;
}
interface IProps {
  sources: ISources[];
  src: string;
  preview_src: string;
  alt?: string;
  className?: string;
}

const LazyLoadImg: FC<IProps> = (props: IProps) => {
  const { className, src, sources, alt, preview_src } = props;
  const [isDone, setIsDone] = useState(false);

  return (
    <div>
      <div
        style={{
          position: 'relative'
        }}
      >
        {!isDone ? (
          <img
            src={preview_src}
            alt={'preview'}
            className={className}
            style={{
              position: 'absolute',
              zIndex: 10
            }}
          />
        ) : null}
        <picture>
          {isDone
            ? sources.map(s => <source srcSet={s.src} type={s.type} />)
            : null}
          <img
            src={src}
            alt={alt}
            className={className}
            onLoad={() => {
              setIsDone(true);
            }}
            style={{
              position: 'absolute',
              zIndex: 1
            }}
          />
        </picture>
      </div>
    </div>
  );
};

export default LazyLoadImg;
