import React, { FC, useRef, useState } from 'react';
import { useComponentDidMount } from '../../../utils/hooks/useComponentDidMount';
// @ts-ignore
import findMainThemeWorker from '../../../utils/workers/findMainTheme.worker';
import analyze from 'rgbaster';
import { css } from 'next/dist/build/webpack/config/blocks/css';

interface IProps {
  src: string;
  alt: string;
}

const AutoBackGroundColorSliderPage: FC<IProps> = (props: IProps) => {
  const { src, alt } = props;
  const [backgroundColor, setBackGroundColor] = useState('#ffffff');
  const worker = useRef(findMainThemeWorker);
  useComponentDidMount(() => {
    const img = new Image();
    img.src = src;
    img.onload = () => {
      if (worker.current) {
        worker.current.postMessage('Hi! From Host');
        worker.current.onmessage = e => {
          console.log(JSON.stringify(e));
        };
      }
      // analyze(src)
      //   .then(value => {
      //     if (value[0]) {
      //       setBackGroundColor(value[0].color);
      //     }
      //   })
      //   .catch(err => null);
    };
  });
  return (
    <div
      className={'section'}
      style={{
        backgroundColor: backgroundColor
      }}
    >
      <img src={src} alt={alt} />
    </div>
  );
};

export default AutoBackGroundColorSliderPage;
