import React, { useContext } from 'react';
import exp from 'constants';

const store = {
  currentTab: {
    currentTab: 0
  },
  test: {
    count: 0
  }
};

const currentTab = (state, action) => {
  if (action.type === 'setCurrentTab') {
    return { ...state, currentTab: action.value };
  } else {
    throw new Error();
  }
};

const test = (state, action) => {
  if (action.type === 'increment') {
    return { ...state, count: state.count + action.value };
  } else {
    throw new Error();
  }
};

// 自定义合并reducer函数
// 根据reducer定义的名称，拿到state中的对应名称下的state
// state 也是一个复合 state
const combineReducers = reducers => (state, action) =>
  Object.keys(reducers)
    // 拿到的state只是属于自己的key的那部分
    .map(k => ({ [k]: reducers[k](state[k], action) }))
    .reduce((acc, cur) => Object.assign({}, acc, cur));

const reducers = combineReducers({ currentTab, test });
console.log(reducers);

let state,
  actions,
  CurrentTabContext,
  hasCalled = false;
export const _useCurrentTab = () => {
  const [state, dispatch] = React.useReducer(reducers, store);
  const actions = {
    setCurrentTab: e => {
      dispatch({ type: 'setCurrentTab', value: e });
    }
  };
  // 创建Context放在最上层父组件
  const CurrentTabContext = React.createContext<{
    state: { [key: string]: any };
    actions: typeof actions;
  }>({ state: state, actions: actions });
  return { state, actions, CurrentTabContext };
};
export const useCurrentTab = () => {
  if (!hasCalled) {
    let currentTab = _useCurrentTab();
    state = currentTab.state;
    actions = currentTab.actions;
    CurrentTabContext = currentTab.CurrentTabContext;
    hasCalled = true;
  }
  return { state, actions, CurrentTabContext };
};

export function createProvider() {
  const [state, dispatch] = React.useReducer(reducers, store);
  const CurrentTabContext = React.createContext<{
    state: typeof state;
    actions: typeof actions;
  }>({ state: state, actions: actions });
}
