import React, { FC } from 'react';
import { useCurrentTab } from '../../utils/hooks/useCurrentTab';

interface IProps {
  children: any;
}

export const ContentSwitcher: FC<IProps> = (props: IProps) => {
  const {
    state: {
      currentTab: { currentTab }
    }
  } = useCurrentTab();
  console.log(currentTab);
  return (
    <div
      style={{
        width: '1600rem',
        overflowX: 'hidden'
      }}
    >
      <div
        style={{
          width: props.children.length * 1600 + 'rem',
          display: 'flex',
          transform: `translateX(1600-${currentTab * 1600}rem)`,
          transition: '0.6s ease-in-out'
        }}
      >
        {props.children
          ? props.children.map((c, i) => (
              <div
                style={{
                  width:  i === currentTab ? '1600rem' : 0,
                  height: i === currentTab ? '100%' : '0',
                  overflow: 'hidden',
                  transition: '0.6s ease-in-out'
                }}
              >
                {c}
              </div>
            ))
          : null}
      </div>
    </div>
  );
};
