import React, { FC, useState } from 'react';

export interface ISources {
  src: string;
  type: string;
}
interface IProps {
  sources: ISources[];
  src: string;
  preview_src: string;
  alt?: string;
  className?: string;
}

const LazyLoadImg: FC<IProps> = (props: IProps) => {
  const { className, src, sources, alt, preview_src } = props;
  const [isDone, setIsDone] = useState(false);

  return (
    <div>
      <div
        // src={preview_src}
        // alt={'preview'}
        className={className}
        style={{
          display: isDone ? 'none' : 'show',
          filter: 'blur(5px)',
        }}
      />
      <picture>
        {sources.map(s => (
          <source srcSet={s.src} type={s.type} />
        ))}
        <img
          src={src}
          alt={alt}
          className={className}
          style={{
            display: isDone ? 'initial' : 'none'
          }}
          onLoad={() => {
            setIsDone(true);
          }}
        />
      </picture>
    </div>
  );
};

export default LazyLoadImg;
