import * as React from 'react';
import styles from './LayoutHeader.module.less';
import { useCallback, useEffect, useState } from 'react';

interface MenuItem {
  key: string;
  label: string;
}

const bodyWidth = document.getElementsByTagName('body')[0].offsetWidth;
console.log(bodyWidth);
const MenuItems: MenuItem[] = [
  {
    key: 'aboutMe',
    label: 'About me'
  },
  {
    key: 'projects',
    label: 'Projects'
  }
];
const LayoutHeader: React.FC<{}> = () => {
  // 设置小圆点的位置
  const [dotX, setDotX] = useState(0);
  const [currentTab, setCurrentTab] = useState(0);
  const menuItemClickHandler = useCallback(
    item => () => {
      if (!item.ref?.current) {
        return;
      }
      console.log(item.ref)
      const offsetLeft = item.ref.current.offsetLeft;
      const offsetWidth = item.ref.current.offsetWidth;
      setDotX(offsetLeft + offsetWidth / 2);
    },
    []
  );
  useEffect(() => {
    menuItemClickHandler(MenuItems[currentTab])
  }, []);
  return (
    <div>
      <div className={styles['layout-menu-container']}>
        {MenuItems.map(i => ({
          ...i,
          ref: React.useRef()
        })).map(item => (
          <span
            key={item.key}
            ref={item.ref}
            className={styles['layout-menu-item']}
            onClick={menuItemClickHandler(item)}
          >
            {item.label}
          </span>
        ))}
      </div>
      <div className={styles['layout-slider']}>
        <span className={styles['layout-slider-dot']} style={{
          transform: `translateX(${dotX}px)`
        }}/>
        <div className={styles['layout-slider-line']} />
      </div>
    </div>
  );
};

export default LayoutHeader;
