import { useEffect, useState } from 'react';

export const useComponentDidMount = (
  handler: () => any,
  postHandler?: () => any
) => {
  const [firstLoaded, setFirstLoaded] = useState(false);
  useEffect(() => {
    if (!firstLoaded) {
      setFirstLoaded(true);
    }
  }, []);
  useEffect(() => {
    if (firstLoaded) {
      handler();
      return postHandler ? () => postHandler() : null;
    }
    return;
  }, [firstLoaded]);
  return {};
};
