import React, { FC, useState } from 'react';
import { useComponentDidMount } from '../../../utils/hooks/useComponentDidMount';

export interface ISources {
  src: string;
  type: string;
}
interface IProps {
  sources: ISources[];
  src: string;
  preview_src: string;
  alt?: string;
  className?: string;
}

const LazyLoadImg: FC<IProps> = (props: IProps) => {
  const { className, src, sources, alt, preview_src } = props;
  const [isDone, setIsDone] = useState(false);
  useComponentDidMount(() => {
    const image = new Image();
    image.src = src;
    image.onload = () => {
      setIsDone(true);
    };
  });

  return (
    <div>
      <picture>
        {isDone
          ? sources.map(s => <source srcSet={s.src} type={s.type} />)
          : null}
        <img src={isDone ? src : preview_src} alt={alt} className={className} />
      </picture>
    </div>
  );
};

export default LazyLoadImg;
