import * as React from 'react';
import styles from './LayoutHeader.module.less';
import { useCallback, useEffect, useRef, useState } from 'react';
import { useDeepCompareEffect } from '../utils/hooks/useDeepCompareEffect';

interface MenuItem {
  key: string;
  label: string;
}

const MenuItems: MenuItem[] = [
  {
    key: 'aboutMe',
    label: 'About me'
  },
  {
    key: 'projects',
    label: 'Projects'
  }
].map(item => ({
  ...item,
  ref: React.useRef()
}));
const LayoutHeader: React.FC<{}> = () => {
  // 设置小圆点的位置
  const [firstLoaded, setFirstLoaded] = useState(false);
  const [dotX, setDotX] = useState(0);
  const [currentTab, setCurrentTab] = useState(0);
  const bodyWidth = useRef(null);
  const menuItemClickHandler = useCallback(
    item => () => {
      if (!firstLoaded) {
        return;
      }
      const pageOffSetLeft = (bodyWidth.current - 1600) / 2;
      const offsetLeft = item.ref.current.offsetLeft;
      const offsetWidth = item.ref.current.offsetWidth;
      setDotX(offsetLeft + offsetWidth / 2 - pageOffSetLeft - 8);
    },
    [bodyWidth, firstLoaded, MenuItems]
  );
  const ManualMenuClickHandler = useCallback(
    item => {
      if (!firstLoaded) {
        return;
      }
      const pageOffSetLeft = (bodyWidth.current - 1600) / 2;
      const offsetLeft = item.ref.current.offsetLeft;
      const offsetWidth = item.ref.current.offsetWidth;
      setDotX(offsetLeft + offsetWidth / 2 - pageOffSetLeft - 8);
    },
    [bodyWidth, firstLoaded, MenuItems]
  );
  useEffect(() => {
    if (!firstLoaded) {
      setFirstLoaded(true);
    }
  }, []);
  useEffect(() => {
    if (firstLoaded) {
      bodyWidth.current = document.getElementsByTagName('body')[0].offsetWidth;
      ManualMenuClickHandler(MenuItems[currentTab]);
    }
  }, [firstLoaded]);
  return (
    <div>
      <div className={styles['layout-menu-container']}>
        {MenuItems.map(item => (
          <span
            key={item.key}
            ref={item.ref}
            className={styles['layout-menu-item']}
            onClick={menuItemClickHandler(item)}
          >
            {item.label}
          </span>
        ))}
      </div>
      <div className={styles['layout-slider']}>
        <span
          className={styles['layout-slider-dot']}
          style={{
            transform: `translateX(${dotX}px)`
          }}
        />
        <div className={styles['layout-slider-line']} />
      </div>
    </div>
  );
};

export default LayoutHeader;
