import React, { FC, useRef, useState } from 'react';
import { useComponentDidMount } from '../../../utils/hooks/useComponentDidMount';
// @ts-ignore
import findMainThemeWorker from 'worker-loader?name=static/[hash].worker.js!../../../utils/workers/findMainTheme.worker';
import analyze from '../../../utils/rgbaster';
import { css } from 'next/dist/build/webpack/config/blocks/css';

interface IProps {
  src: string;
  alt: string;
}

const AutoBackGroundColorSliderPage: FC<IProps> = (props: IProps) => {
  const { src, alt } = props;
  const [backgroundColor, setBackGroundColor] = useState('#ffffff');
  useComponentDidMount(() => {
    const img = new Image();
    img.src = src;
    img.onload = () => {
      // const worker = new findMainThemeWorker();
      // worker.postMessage(src);
      // worker.onmessage = e => {
      //   console.log(e);
      //   console.log(JSON.stringify(e));
      // };
      analyze(src, img)
        // analyze(src)
        .then(value => {
          if (value[0]) {
            setBackGroundColor(value[0].color);
          }
        })
        .catch(err => console.log(err));
    };
  });
  return (
    <div
      className={'section'}
      style={{
        backgroundColor: backgroundColor
      }}
    >
      <img src={src} alt={alt} />
    </div>
  );
};

export default AutoBackGroundColorSliderPage;
