import { useEffect, useState } from 'react';
export const useComponentDidMount = (handler, postHandler) => {
  const {
    0: firstLoaded,
    1: setFirstLoaded
  } = useState(false);
  const {
    0: IsDone,
    1: setIsDone
  } = useState(false);
  useEffect(() => {
    if (!firstLoaded) {
      setFirstLoaded(true);
    }
  }, []);
  useEffect(() => {
    if (firstLoaded) {
      handler();
      setIsDone(true);
      return postHandler ? () => postHandler() : () => null;
    }

    return;
  }, [firstLoaded]);
  return {
    firstLoaded,
    IsDone,
    setIsDone
  };
};
